INSERT INTO [dbo].[Authors] ([AuthorPicture], [FullName], [AuthorBooks]) VALUES (N'https://m1.paperblog.com/i/281/2814387/cassandra-clare-L-sPMKwo.png',																																  N'Cassandra Clare', N'The Mortal Instruments, The Dark Artifices, The Last Hours');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://cdn.penguin.co.uk/dam-assets/books/9780141325507/9780141325507-jacket-large.jpg',											N'The Red Pyramid',		  N'Rick Riordan',    N'The Kane Chronicles',        N'10',	   N'9',  N'Disney Hyperion',			  N'One night, Dr. Kane brings the siblings together for a "research experiment" at the British Museum, where he hopes to set things right for his family. Instead, he unleashes the Egyptian god Set, who banishes him to oblivion and forces the children to flee for their lives.');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/91tJAt3Yk7L.jpg',												N'The Throne of Fire',	  N'Rick Riordan',    N'The Kane Chronicles',        N'15', N'9',  N'Disney Hyperion',			  N'In this exciting second installment of the three-book series, Carter and Sadie, offspring of the brilliant Egyptologist Dr. Julius Kane');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://books.disney.com/content/uploads/2017/08/9781484781326-327x462.jpg',														N'The Serpent`s Shadow',  N'Rick Riordan',    N'The Kane Chronicles',        N'13', N'9',  N'Disney Hyperion',			  N'He`s b-a-a-ack! Despite their best efforts, Carter and Sadie Kane can`t seem to keep Apophis, the chaos snake, down.');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/91GXC09xGBL._AC_UF1000,1000_QL80_.jpg',						N'Library of Souls',	  N'Ransom Riggs',    N'Home for Peculiar Children', N'14', N'5',  N'Quirk Books',				  N'A boy with extraordinary powers. An army of deadly monsters. An epic battle for the future of peculiardom.');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/518WxaFw0rL._AC_UF1000,1000_QL80_.jpg',						N'The Martian Chronicles',N'Ray Bradbury',	  N'-',							 N'9',  N'4',  N'Audible Studios',			  N'Mars was a distant shore, and the men spread upon it in wave.... Each wave different, and each wave stronger.');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/61z7RDG3OIL._AC_UF1000,1000_QL80_.jpg',						N'Fahrenheit 451',		  N'Ray Bradbury',    N'-',							 N'8',  N'4',  N'Simon & Schuster',			  N'Guy Montag is a fireman. His job is to destroy the most illegal of commodities, the printed book, along with the houses in which they are hidden.');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/71kYIeAK8yL._AC_UF1000,1000_QL80_.jpg',						N'The Illustrated Man',   N'Ray Bradbury',    N'-',							 N'10', N'4',  N'Simon & Schuster',			  N'The Illustrated Man is a kaleidoscopic blending of magic, imagination, and truth');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'http://starcrossedbookblog.com/wp-content/uploads/2018/12/813Wa1PogL.jpg',															N'City of Bones',     	  N'Cassandra Clare', N'The Mortal Instruments',	 N'13', N'9',  N'Margaret K. McElderry Books', N'When fifteen-year-old Clary Fray heads out to the Pandemonium Club in New York City, she hardly expects to witness a murder');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://kbimages1-a.akamaihd.net/fdbceff7-ef98-4204-af13-1b4e6c10db3f/1200/1200/False/city-of-ashes-4.jpg',							N'City of Ashes',		  N'Cassandra Clare', N'The Mortal Instruments',	 N'22', N'9',  N'Margaret K. McElderry Books', N'Clary Fray just wishes that her life would go back to normal. But what`s normal when you`re a demon-slaying Shadowhunter, your mother is in a magically induced coma, and you can suddenly see Downworlders like werewolves, vampires, and faeries?');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/81f73W7uSxL.jpg',												N'City of Glass',		  N'Cassandra Clare', N'The Mortal Instruments',	 N'24', N'9',  N'Margaret K. McElderry Books', N'Love is a mortal sin, and the secrets of the past are deadly. Plunge into the third installment in the internationally bestselling Mortal Instruments series and “prepare to be hooked” ');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://images.thalia.media/-/BF2000-2000/d9133e89d2e34ab5a8642027cc6724a4/city-of-fallen-angels-taschenbuch-cassandra-clare.jpeg', N'City of Fallen Angels', N'Cassandra Clare', N'The Mortal Instruments',	 N'20', N'9',  N'Margaret K. McElderry Books', N'With Valentine out of the way, Clary thought she and Jace could finally be together--but when someone starts murdering the Shadowhunters ');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/81ugWwaH2+L.jpg',												N'City of Lost Souls',	  N'Cassandra Clare', N'The Mortal Instruments',	 N'13', N'9',  N'Margaret K. McElderry Books', N'When Jace and Clary meet again, Clary is horrified to discover that the demon Lilith’s magic has bound her beloved Jace together with her evil brother Sebastian, and that Jace has become a servant of evil.');
INSERT INTO [dbo].[Books] ([ImgUrl], [Title], [AuthorName], [Series], [Price], [Genre], [PublishingHouse], [Description]) VALUES (N'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/51T+BEX3umL.jpg',												N'City of Heavenly Fire', N'Cassandra Clare', N'The Mortal Instruments',	 N'13', N'9',  N'Margaret K. McElderry Books', N'Darkness has descended on the Shadowhunter world. Chaos and destruction overwhelm the Nephilim as Clary, Jace, Simon, and their friends band together to fight the greatest evil they have ever faced: Clary’s own brother.');
INSERT INTO [dbo].[Publishers] ([Title], [Bio]) VALUES (N'Disney Hyperion',				N'The one you can’t put down. The one you’ll never forget. The one that makes you laugh out loud. The one that made you sob even louder.');
INSERT INTO [dbo].[Publishers] ([Title], [Bio]) VALUES (N'Audible Studios',				N'Audible Originals are exclusive audio titles created by celebrated storytellers from worlds as diverse as theater, journalism, literature and more, all produced in the Audible studios.');
INSERT INTO [dbo].[Publishers] ([Title], [Bio]) VALUES (N'Simon & Schuster',			N'Some best-selling titles include Heart Bones, It Starts with Us genre busting horror novels Beneath the Stairs and The Orinoco Uranium');
INSERT INTO [dbo].[Publishers] ([Title], [Bio]) VALUES (N'Margaret K. McElderry Books', N'Founded by legendary editor Margaret K. McElderry in 1972, Margaret K. McElderry Books is a boutique imprint of Simon & Schuster’s Children’s Division, recognized internationally as a publisher of literary author-driven fiction and nonfiction for the teen, middle grade, picture book, and poetry markets. ');
INSERT INTO [dbo].[Publishers] ([Title], [Bio]) VALUES (N'Quirk Books',					N'Some of Quirk’s best-selling titles include Miss Peregrine’s Home for Peculiar Children, Grady Hendrix’s genre busting horror novels My Best Friend’s Exorcism and The Southern Book Club’s Guide to Slaying Vampires, the legendary mash-up Pride and Prejudice and Zombies, The Baby Owner’s Manual, and The Astrology of You and Me. While headquartered on a cobblestone street in historic Old City, Philadelphia, Quirk’s books are distributed worldwide by Penguin Random House and available wherever books are sold.');
